{"filter":false,"title":"image_replacement_lib.py","tooltip":"/workshop/labs/image_replacement/image_replacement_lib.py","undoManager":{"mark":4,"position":4,"stack":[[{"start":{"row":0,"column":0},"end":{"row":25,"column":0},"action":"insert","lines":["import boto3","import json","import base64","from io import BytesIO","from random import randint","","","#get a BytesIO object from file bytes","def get_bytesio_from_bytes(image_bytes):","    image_io = BytesIO(image_bytes)","    return image_io","","","#get a base64-encoded string from file bytes","def get_base64_from_bytes(image_bytes):","    resized_io = get_bytesio_from_bytes(image_bytes)","    img_str = base64.b64encode(resized_io.getvalue()).decode(\"utf-8\")","    return img_str","","","#load the bytes from a file on disk","def get_bytes_from_file(file_path):","    with open(file_path, \"rb\") as image_file:","        file_bytes = image_file.read()","    return file_bytes",""],"id":1}],[{"start":{"row":25,"column":0},"end":{"row":26,"column":0},"action":"insert","lines":["",""],"id":2}],[{"start":{"row":26,"column":0},"end":{"row":50,"column":0},"action":"insert","lines":["#get the stringified request body for the InvokeModel API call","def get_titan_image_inpainting_request_body(prompt, image_bytes=None, mask_prompt=None, negative_prompt=None):","    input_image_base64 = get_base64_from_bytes(image_bytes)","    ","    body = { #create the JSON payload to pass to the InvokeModel API","        \"taskType\": \"INPAINTING\",","        \"inPaintingParams\": {","            \"image\": input_image_base64,","            \"maskPrompt\": mask_prompt,","        },","        \"imageGenerationConfig\": {","            \"numberOfImages\": 1,  # Number of variations to generate","            \"quality\": \"premium\",  # Allowed values are \"standard\" and \"premium\"","            \"height\": 512,","            \"width\": 512,","            \"cfgScale\": 8.0,","            \"seed\": randint(0, 100000),  # Use a random seed","        },","    }","    ","    if prompt:  #Indicate what we want to insert where the masked item(s) were (blank to remove an item)","        body['inPaintingParams']['text'] = prompt #if prompt is missing, we will just remove the item(s) indicated in the mask prompt","    ","    return json.dumps(body)",""],"id":3}],[{"start":{"row":50,"column":0},"end":{"row":51,"column":0},"action":"insert","lines":["",""],"id":4},{"start":{"row":51,"column":0},"end":{"row":52,"column":0},"action":"insert","lines":["",""]}],[{"start":{"row":52,"column":0},"end":{"row":77,"column":0},"action":"insert","lines":["#get a BytesIO object from the Titan Image Generator response","def get_titan_response_image(response):","","    response = json.loads(response.get('body').read())","    ","    images = response.get('images')","    ","    image_data = base64.b64decode(images[0])","","    return BytesIO(image_data)","","","#generate an image using Amazon Titan Image Generator","def get_image_from_model(prompt_content, image_bytes, mask_prompt=None):","    session = boto3.Session()","","    bedrock = session.client(service_name='bedrock-runtime') #creates a Bedrock client","    ","    body = get_titan_image_inpainting_request_body(prompt_content, image_bytes, mask_prompt=mask_prompt)","    ","    response = bedrock.invoke_model(body=body, modelId=\"amazon.titan-image-generator-v1\", contentType=\"application/json\", accept=\"application/json\")","    ","    output = get_titan_response_image(response)","    ","    return output",""],"id":5}]]},"ace":{"folds":[],"scrolltop":903,"scrollleft":0,"selection":{"start":{"row":77,"column":0},"end":{"row":77,"column":0},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":{"row":31,"state":"start","mode":"ace/mode/python"}},"timestamp":1737197729705,"hash":"5e40756eb94d590e455ac2e1626fea73da56792d"}